CREATE TABLE atTemp ( atTemp_id VARCHAR(15) UNIQUE NOT NULL, atTemp_begin DATETIME, atTemp_end DATETIME NULL, atTemp_type VARCHAR(24) NOT NULL DEFAULT('time'), atTemp_reason VARCHAR(15) NULL, atTemp_remark TEXT NULL, atTemp_target VARCHAR(15) NULL DEFAULT NULL, atTemp_beginTS INTEGER NULL, atTemp_endTS INTEGER NULL, atTemp_diff INTEGER NULL);
CREATE TABLE atCondition (atCondition_id VARCHAR(15) UNIQUE NOT NULL, atCondition_name TEXT NOT NULL, atCondition_begin DATETIME NULL, atCondition_end DATETIME NULL, atCondition_weekHours INTEGER NULL, atCondition_vacations INTEGER NULL, atCondition_holidaysCost INTEGER NULL, atCondition_beginNight INTEGER NULL, atCondition_endNight INTEGER NULL, atCondition_nightCost INTEGER NULL, atCondition_sundayCost INTEGER NULL, atCondition_default INTEGER DEFAULT 0);
CREATE TABLE atEntity (atEntity_id VARCHAR(15) UNIQUE NOT NULL, atEntity_workTime INTEGER NOT NULL DEFAULT(100), atEntity_vacations INTEGER NOT NULL DEFAULT(20), atEntity_type TEXT NOT NULL, atEntity_commonName TEXT NULL DEFAULT(NULL), atEntity_URL TEXT NULL, atEntity_remoteService TEXT NULL, atEntity_condition VARCHAR(15) DEFAULT NULL, atEntity_beginDate DATETIME NULL);
CREATE TABLE atHoliday (atHoliday_id VARCHAR(15) UNIQUE NOT NULL, atHoliday_day DATETIME NOT NULL, atHoliday_name TEXT NULL);
CREATE TABLE atValidation ( atValidation_id VARCHAR(15) UNIQUE NOT NULL, atValidation_target VARCHAR(15) NOT NULL, atValidation_month INTEGER, atValidation_year INTEGER, atValidation_vacations FLOAT, atValidation_worktime FLOAT, atValidation_todo FLOAT, atValidation_overtime FLOAT );
CREATE TABLE hProject (hProject_id INTEGER PRIMARY KEY AUTOINCREMENT, hProject_name VARCHAR(255), hProject_closed DATETIME NULL, hProject_opened DATETIME NULL, hProject_targetEnd DATETIME NULL);
CREATE TABLE hTime ( hTime_id INTEGER PRIMARY KEY AUTOINCREMENT, hTime_duration INTEGER, hTime_begin DATETIME NULL, hTime_end DATETIME NULL, hTime_entity VARCHAR(15), hTime_project INTEGER, hTime_coment TEXT);
